    }

    render() {
        return(
            <div className="card">
                <form action="" className="admin-form ajouter-recette">
                    <input 
                    value={this.state.nom} onChange={this.handleChange} name="nom" type="text" placeholder="nom de la recette" />
                    <input value={this.state.image} onChange={this.handleChange} name="image" type="text" placeholder="nom de l'image" />
                    <textarea value={this.state.ingredients} onChange={this.handleChange} name="ingredients" id="" rows="3" placeholder="Liste des ingredients" />
                    <textarea value={this.state.instructions} onChange={this.handleChange} name="instructions" id="" rows="15" placeholder="Liste des instructions" />
                </form>
            </div>
        )
    }
}

export default AjouterRecette
